import './App.css';
//importamos le ponemos un nombre y la direccion de la carpeta
import web from './imagenes/web.png'
//importar el boton 
import Boton from './componente/Boton';

import Contador from './componente/contador';

function App() {
//crear la funcion boton click
const click = ()=>{
  alert('estas haciendo click');
}

const reiniciar = ()=>{
  alert('estas haciendo reiniciando');
}


  return (
    <div className="App">
    <div className='logo-contenedor'>
      <img 
      className='logo'
      //nombre de esa variable 
      src={web}
      alt='logo del contador'/>
      </div>
      <div>
        <Boton
        texto={'click'}
        click={true}
        controlclick={click}/>
         <Boton
        texto={'reiniciar'}
        click={false}
        controlclick={reiniciar}/>


      </div>
    </div>
  );
}

export default App;
